mapper.select:
select count(rvw_idx)
from review_tb
where prd_idx=#{prd_idx}

mapper.update:
update review_tb
set rvw_content=#{rvw_content}, rvw_rating=#{rvw_rating}
where rvw_idx=#{rvw_idx}

mapper.select:
select avg(rvw_rating)*2
from review_tb
where prd_idx=#{prd_idx}

select rvw_idx, mem_id, prd_idx, rvw_content, rvw_rating, rvw_regdate
from (select rvw_idx, mem_id, prd_idx, rvw_content, rvw_rating, rvw_regdate,
             row_number() over(order by rvw_idx desc) r_seq
      from review_tb
      where prd_idx=#{prd_idx})
where r_seq between #{cri.rowStart} and #{cri.rowEnd}
